import { Component, Input, OnInit } from '@angular/core';
import { Groupe } from 'src/groupe';

@Component({
  selector: 'app-user',
     templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})
export class UserComponent implements OnInit {

  id_user : string="";
  mdp : string="";
  @Input() groupes: Groupe[] = [];

  constructor() { }

  ngOnInit() {
  }
  getId_user(){
    return this.id_user;
  }

  rejoindreGroupe(idg : number){

      this.groupes.forEach(groupe => {
        if(groupe.id_groupe === idg ){
          if(groupe.liste_user.length-1 <= groupe.nbr_user){
            groupe.liste_user.push(this.id_user);
          }
          else{
            console.log("Le groupe est complet");
          }

        }


      })
  }

}
<p>
  Composant user
  {{groupes|json}}
</p>

<div *ngFor="let groupe of groupes; index as i">
  <h2>Groupe {{groupe.id_groupe}}</h2>
  <div *ngFor ="let user of groupe.liste_user; index as j">
    <table mat-table [groupes] class="mat-elevation-z8">
      <ng-container matColumnDef="user">
        <th mat-header-cell *matHeaderCellDef> user </th>
        <td mat-cell *matCellDef="user"> {{user}} </td>
      </ng-container>
      <ng-container matColumnDef="join">
        <th mat-header-cell *matHeaderCellDef> join </th>
        <td mat-cell *matCellDef="user"> <button (click)="rejoindreGroupe(groupe.id_groupe)">join</button>
        </td>
      </ng-container>

    </table>

  </div>
