import { Component, Input, OnInit } from '@angular/core';
import { Groupe } from 'src/groupe';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})
export class UserComponent implements OnInit {

  id_user : string="";
  mdp : string="";
  @Input() groupes: Groupe[] = [];

  constructor() { }

  ngOnInit() {
  }
  getId_user(){
    return this.id_user;
  }
  rejoindreGroupe(): Promise<Groupe[]>{
    return new Promise((resolve) => {
      this.groupes.forEach
      //trouver le groupe avec le moins d'utilisateur
      let min = this.groupes[0].nbr_user;
      let idG = 0;
      for(let i = 0; i < this.groupes.length; i++){
        if(this.groupes[i].nbr_user < min){
          min = this.groupes[i].nbr_user;
          idG = i;
        }
      }
      //ajouter l'utilisateur au groupe
      this.groupes[idG].liste_user.push(this.id_user);
      this.groupes[idG].nbr_user += 1;
      resolve(this.groupes);
    });
  }
}
