import { Component, Input, OnInit } from '@angular/core';
import { Groupe } from 'src/groupe';
import { User } from 'src/user';

@Component({
  selector: 'app-user',
     templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})
export class UserComponent implements OnInit {

  user : User = {
    id_user : 1,
    nom : "user1",
    mdp : "mdp1"
  }
  @Input() groupes: Groupe[] = [];

  constructor() { }

  ngOnInit() {
  }
  getId_user(){
    return this.user.id_user;
  }
  //verifier si un utilisateur est deja dans un groupe
  verifUserInGroupe(idg : number){
    this.groupes.forEach(groupe => {
      if(groupe.id_groupe === idg ){
        groupe.liste_user.forEach(user => {
          if(user.id_user === this.user.id_user){
            return true;
          }
          else
        });
      }
    });

    return false;
  }



  rejoindreGroupe(idg : number){

      this.groupes.forEach(groupe => {
        if(groupe.id_groupe === idg ){
          if(groupe.liste_user.length-1 <= groupe.nbr_user ){
            if(this.verifUserInGroupe(idg) === false){
            groupe.liste_user.push(this.user);
            }
            else{
              console.log("Vous etes deja dans ce groupe");
            }
          }
          else{
            console.log("Le groupe est complet");
          }

        }


      })
  }

}
<p>
  Composant user
  {{groupes|json}}
</p>

<div *ngFor="let groupe of groupes; index as i">
  <h2>Groupe {{groupe.id_groupe}}</h2>
    <table>

      <tr *ngFor="let user of groupe.liste_user; index as j">
        <td>
          {{user}}
        </td>
      </tr>
      <button (click)="rejoindreGroupe(groupe.id_groupe)">Join</button>
    </table>

</div>
