import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
//Classe adminn avec un id et un mot de passe
//Intialiser les valeurs de id et mdp
export class AdminComponent implements OnInit {
  id: string = "admin";
  mdp: string = "admin";

  //Recevoir en entrée le nombre d'utilisateurs
  nbUsers: string = "";
  nbGroupes: string = "";

  //Choisir en entrée une configuration LASTMIN ou LASTMAX
  config: string = "LASTMIN";

  constructor() { }

  ngOnInit() {
  }

  //Méthode pour vérifier si l'admin est connecté
  //Si oui, on affiche la page admin
  //Sinon, on affiche un message d'erreur
  connecter() {
    if (this.id == "admin" && this.mdp == "admin") {
      console.log("Connecté");
    }
    else {
      console.log("Erreur");
    }
  }
  //recuperer le nombre d'utilisateurs
  getNbUsers() {
    return this.nbUsers;
  }
  //modifier le nombre d'utilisateurs
  setNbUsers(nbUsers: string) {
    this.nbUsers = nbUsers;
  }
  //recuperer le nombre de groupes
  getNbGroupes() {
    return this.nbGroupes;
  }
  //modifier le nombre de groupes
  setNbGroupes(nbGroupes: string) {
    this.nbGroupes = nbGroupes;
  }
  //recuperer la configuration
  getConfig() {
    return this.config;
  }
  //modifier la configuration
  setConfig(config: string) {
    this.config = config;
  }



}
<form>
  <h1>Admin</h1>
</form>

<div class="container">
  <div class="brand-logo"></div>
  <div class="brand-title">ADMIN</div>
  <div class="inputs">
    <label>Nombre de groupe</label>
    <input type="text" [(ngModel)]="nbGroupes" name="nbGroupes" placeholder="Entrez un nombre">
    <label>Nombre d'utilisateurs</label>
    <input type="text" placeholder="Min 6 charaters long" />
    <button type="submit">LOGIN</button>
  </div>
  <a href="https://twitter.com/prathkum">MADE BY PRATHAM</a>
</div>
